import React from "react";
import { Box, Button, Form, FormField, Header, TextInput } from "grommet";

async function signUp(username) {
const response = await fetch(`http://localhost:5000/users/add/`,{
    method: 'POST',
    headers: {
        'Content-Type': 'application/json'
    },
    body: JSON.stringify(username)
});
return response.json();
}

const Signup = () => {
  return (
    <>
      <Box  justify="center" align="center">
        <Box  justify="center" align="center" width="medium">
          <Header > Sign up </Header>
          <Form
            onChange={(value) => console.log("Change", value)}
            onSubmit={(event) => {
              console.log("Submit", event.value, event.touched)
              signUp(event.value.username)
            }
            }
          >
            <FormField>
              <TextInput name="username" placeholder="Username" />
            </FormField>
            <FormField>
              <TextInput name="fullName" placeholder="Full Name" />
            </FormField>

            <FormField>
              <TextInput name="email" placeholder="Email" />
            </FormField>
            <FormField>
              <TextInput
                placeholder="Password"
                name="password"
                htmlFor="password"
                type="password"
              />
            </FormField>
            <FormField>
              <TextInput
                placeholder="Confirm Password"
                name="password"
                htmlFor="password"
                type="password"
              />
            </FormField>
            <Box gap="small" direction="row">
            <Button color="#D81159" label="Cancel" />
            <Button color="#D81159" primary label="Continue" />
            </Box>
          </Form>
        </Box>
      </Box>
    </>
  );
};

export default Signup;
