import kitty from "./petImages/kittysad.jpg";
import killua from "./petImages/killua.jpg";
import appa from "./petImages/appa.jpg";
import luna from "./petImages/luna.png";
import sprinkles from "./petImages/sprinkles.JPG";

import React from "react";
import PetProfile from "./PetProfile";
import { Search } from "grommet-icons";
import { connect } from "react-redux";
import { getPets, scrapeWebByLocation } from "./js/actions/index";
import { 
  Button, 
  Heading, 
  Image, 
  Box, 
  Grid,
  FormField, 
  TextInput } from "grommet";

/*function testing(city){
  console.log('here')
fetch(`http://localhost:5000/find_pets_py/${city}`)
  .then((response) => response.json())
  .then((json) => {
    console.log(json);
  });
}*/

class PetProfiles extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "", pets: this.props.petData};
  }
  componentDidMount() {
    this.props.getPets();
    console.log(this.props.petData);
    this.setState({pets: this.props.petData})
    console.log(this.state.pets);
  }

  render() {
    return (
      <>
        <Box align="center" justify="center">
          <Heading align="center" justify="center">
            Search for a fluff
          </Heading>
          <Box direction="row" align="center">
            <TextInput
              type="search"
              placeholder="search by city"
              value={this.state.value}
              onChange={(event) => {
                this.setState({ value: event.target.value });
                console.log(this.state.value);
              }}
            />
            <Search
              color="brand"
              type="submit"
              onClick={() => {
                this.props.scrapeWebByLocation(this.state.value);
              }}
            />
          </Box>
          {/*<Button onClick={ ()=> {testing('jacksonville')}}/>*/}
          <Box > 
          <Grid columns={"large"/*size !== 'small' ? 'small' : '100%'*/} gap="small">
          {/*}  <PetProfile image={kitty} name={"Sade"} />
            <PetProfile image={killua} name={"Killua"} />
            <PetProfile image={appa} name={"Appa"} />
            <PetProfile image={luna} name={"Luna"} />
            <PetProfile image={sprinkles} name={"Sprinkles"} />
            */}
           {/* <PetProfile
                //key={pets.date}
                image={kitty}
                name={this.state.pets.name?this.state.pets.name:'NULL'}
                location={this.state.pets.location}
                size={this.state.pets.size}
                species={this.state.pets.species}
                breed={this.state.pets.breed}
                og_post={this.state.og_post}
                comment={this.state.comments}
           />*/}
            {this.props.petData.map((pets) => (
              <PetProfile
                key={pets.date}
                image={pets.image? pets.image: luna}
                name={pets.name?pets.name:''}
                location={pets.location}
                size={pets.size}
                species={pets.species}
                breed={pets.breed}
                date={pets.date}
                comments={pets.comments}
                og_post={pets.og_post}
              />
            ))}
                    </Grid>

          </Box>
        </Box>
      </>
    );
  }
}

function mapStateToProps(state) {
  return {
    petData: state.petData,
    isFetching: state.isFetching,
  };
}
export default connect(mapStateToProps, { getPets, scrapeWebByLocation })(
  PetProfiles
);
