import React from 'react';
import {
    Box,
    Grommet,
    Header,
Form,
FormField,
RangeInput,
RadioButtonGroup,
Select,
Text,
TextInput,
} from 'grommet';
import {
    Upload,
}from 'grommet-icons';

const FluffForm = () => {
    const [value, setValue] = React.useState({ name: ''});
    const [species, setSpecies] = React.useState('');
    const catBreeds = ["Siamese", "Calico", "Sphinx", "Sad"]
    const dogBreeds = ["Poodle", "Pitbull", "Chihuahua", "Shiba Inu"]
    const options = ["Brawley, CA", "Chico, CA", "Sacramento CA", "El Centro, CA"]
    return(
        <Grommet>
            <Box justify="center" align="center">
            <Header level={1}> Submit a Fluff </Header>
            </Box>
            <Box justify="center" align="center">
            <Form
                value = {value}
                onChange={nextValue => setValue(nextValue)}
            onSubmit={({ value: nextValue }) => console.log(nextValue)}
                >
            <FormField label="Name" name="name" required>
                <TextInput name="name"/>
            </FormField>
            <FormField label="Size" name="size" pad>
              <RangeInput name="size" min={15} max={75} step={1} />
            </FormField>
            <FormField name="species">
              <RadioButtonGroup 
              name="species" 
              options={['dog', 'cat']} 
              value={value}
              onChange={(event)=>{
                  console.log(event.target.value)
                  setSpecies(event.target.value)
                }}
              />
              {species !== '' &&
               <Select
               name="select"
               placeholder="Select Breed"
               value={value}
               options={species === 'cat' ? catBreeds : dogBreeds}
               onChange={({ breed}) => setValue(breed)}
             />
              }
            </FormField>
            <Select
          id="select"
          name="select"
          placeholder="Select"
          value={value}
          options={options}
          onChange={({ option }) => setValue(option)}
        />
        <FormField label="Comments" name="comments">
              <TextArea name="comments" />
            </FormField>
        <Box background="#d81159" align="center" onClick={()=>{console.log('uploading...')}}>
            <Text>Upload Image</Text>
            <Box >
            <Upload background="#d81159"/>
            </Box>
        </Box>
            </Form>
            </Box>
        </Grommet>
    );
};

export default FluffForm;
