import React from 'react';
import PropTypes from 'prop-types';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import {
  Box,
  Card, 
  CardBody, 
  CardFooter,
  Heading,
  Grommet,
  Text,
 } from 'grommet';
  import {
    Gremlin
  } from 'grommet-icons';
  import PetProfiles from './PetProfiles';
  import FluffForm from './FluffForm';
  import './index.css';
  import AppHeader from './AppHeader';
  import Login from './Login';
  import Signup from './Signup';
  import List from './List';
  import Announcer from './Announcement';


  const Identifier = ({ children, title, subTitle, size, ...rest }) => (
    <Box gap="small" align="center" direction="row" pad="small" {...rest}>
      {children}
      <Box>
        <Text size={size} weight="bold">
          {title}
        </Text>
        <Text size={size}>{subTitle}</Text>

      </Box>
    </Box>
  );


function App({...props}) {
  const options = ['location', 'size']
  return (
    <Router>
    <Grommet full>
      <AppHeader />
    <Box>

</Box>
<Box justify="center" align="center" background="#fbb02d">
  <Heading color="white">{/*You aint :-o Nicki you :-o Nicole body*/}</Heading>
  {props.kitty}
<Announcer />

<List />

</Box>
<Box direction="row" align="center" justify="center" pad="large">
  <Link to ="/petprofile">
<Card
pad="large"
          >
            <CardBody pad="small">
              <Identifier
                title={"Find a Fluff"}
                subTitle={"Search for a fluffy friend"}
                size="xlarge"
              >
                {<Gremlin/>}
              </Identifier>
            </CardBody>
            <CardFooter pad={{ horizontal: 'medium', vertical: 'small' }}>
              <Text size="xsmall">{"Have you lost your pet?"}</Text>
            </CardFooter>
          </Card>
          </Link>
          <Link to="/findafluff">
          <Card
          pad="large"
          >
            <CardBody pad="small">
              <Identifier
                title={"Share a Fluff"}
                subTitle={"Tell us if you have spotted a lost pet"}
                size="xlarge"
              >
                {<Gremlin/>}
              </Identifier>
            </CardBody>
            <CardFooter pad={{ horizontal: 'medium', vertical: 'small' }}>
              <Text size="xsmall">{"Have you found someones fluff?"}</Text>
            </CardFooter> 
          </Card>
          </Link>
          </Box>

    <Switch>
      <Route exact path="/findafluff">
        <FluffForm />
      </Route>
      <Route path="/login">
        <Login/>
        </Route>
        <Route path="/petprofile">
        <PetProfiles/>
        </Route>
        <Route path="/signup">
          <Signup />
        </Route>
    </Switch>
    </Grommet>
    </Router>
  );
}

export default App;
