import React from 'react';
import {
  Anchor,
  Box,
  Card, 
  CardBody, 
  CardFooter,
  Header,
  Grommet,
  Select,
  Text,
  Nav} from 'grommet';
  import {
    Gremlin
  } from 'grommet-icons';
import logo from './logo.svg';
import './App.css';
const Identifier = ({ children, title, subTitle, size, ...rest }) => (
  <Box gap="small" align="center" direction="row" pad="small" {...rest}>
    {children}
    <Box>
      <Text size={size} weight="bold">
        {title}
      </Text>
      <Text size={size}>{subTitle}</Text>
    </Box>
  </Box>
);

function App() {
  const options = ['location', 'size']
  return (
    <Grommet full>
      <>
      <Header background="light-4" pad="small">
        <Text weight="bold">Fluff Finder</Text>
      <Nav direction="row">
        <Anchor label="Home" href="#" />
        <Anchor label="Profile" href="#" />
      </Nav>
    </Header>    
    <Box>
    <Select
    id="text-input"
    options={options}
    placeholder="Search"
/>
</Box>
<Box direction="row">
<Card
            onClick={() => {

              alert('Card was Clicked!');
            }}
          >
            <CardBody pad="small">
              <Identifier
                title={"Find a Fluff"}
                subTitle={"Search for a fluffy friend"}
                size="xlarge"
              >
                {<Gremlin/>}
              </Identifier>
            </CardBody>
            <CardFooter pad={{ horizontal: 'medium', vertical: 'small' }}>
              <Text size="xsmall">{"Have you lost your pet?"}</Text>
            </CardFooter>
          </Card>
          <Card
            onClick={() => {

              alert('Card was Clicked!');
            }}
          >
            <CardBody pad="small">
              <Identifier
                title={"value.title"}
                subTitle={"value.subTitle"}
                size="small"
              >
                {<Gremlin/>}
              </Identifier>
            </CardBody>
            <CardFooter pad={{ horizontal: 'medium', vertical: 'small' }}>
              <Text size="xsmall">{"value.message"}</Text>
            </CardFooter>
          </Card>
          </Box>
      </>
    </Grommet>
  );
}

export default App;
