import React from 'react';
import { Widget } from 'react-chat-widget';
import PropTypes from 'prop-types';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import {
  Box,
  Card, 
  CardBody, 
  CardFooter,
  Heading,
  Grommet,
  Text,
 } from 'grommet';
  import {
    Gremlin
  } from 'grommet-icons';
  import PetProfiles from './PetProfiles';
  import FluffForm from './FluffForm';
  import './index.css';
  import AppHeader from './AppHeader';
  import Login from './Login';
  import Signup from './Signup';
  import UserProfile from './UserProfile';
  import List from './List';
  import Announcer from './Announcement';
import Identifier from './Identifier';
import myTheme from './myTheme.js';




function App({...props}) {
  const options = ['location', 'size']
  return (
    <Router>
    <Grommet theme={myTheme} full>
      <AppHeader />
<Box justify="center" align="center" background="#fbb02d">
  <Heading color="white">{/*You aint :-o Nicki you :-o Nicole body*/}</Heading>
<Announcer />

<List />

</Box>
<Box direction="row" align="center" justify="center" pad="large" gap="large">
  <Link to ="/petprofile">
  <Card
          pad="large"
          >
            <CardBody pad="small">
              <Identifier
                title={"Find a Fluff"}
                subTitle={"Have you found someones cute"}
                size="xlarge"
              >
                {<Gremlin/>}
              </Identifier>
            </CardBody>
            <CardFooter pad={{ horizontal: 'medium', vertical: 'small' }}>
              <Text size="xsmall">{"Are you looking for your cute?"}</Text>
            </CardFooter> 
          </Card>
          </Link>
          <Link to="/findafluff">
          <Card
          pad="large"
          >
            <CardBody pad="small">
              <Identifier
                title={"Share a Fluff"}
                subTitle={"Tell us if you have spotted a lost pet"}
                size="xlarge"
              >
                {<Gremlin/>}
              </Identifier>
            </CardBody>
            <CardFooter pad={{ horizontal: 'medium', vertical: 'small' }}>
              <Text size="xsmall">{"Have you found someones fluff?"}</Text>
            </CardFooter> 
          </Card>
          </Link>
          </Box>
          <Link to="userprofile">
<Gremlin/>
</Link>
    <Switch>
      <Route exact path="/findafluff">
        <FluffForm />
      </Route>
      <Route path="/login">
        <Login/>
        </Route>
        <Route path="/petprofile">
        <PetProfiles/>
        </Route>
        <Route path="/signup">
          <Signup />
        </Route>
        <Route path="/userprofile">
          <UserProfile />
        </Route>
    </Switch>
    </Grommet>
    </Router>
  );
}

export default App;
