import React from "react";
import { Box, Button, Form, FormField, Header, TextInput } from "grommet";
import { connect } from "react-redux";
import { signUp } from '../js/actions/index';

const Signup = ({...props}) => {
  return (
    <>
      <Box justify="center" align="center">
        <Box justify="center" align="center" width="medium">
          <Header> Sign up </Header>
          <Form
            //onChange={(value) => {console.log("Change", value)}}
            onSubmit={(event) => {
              console.log("Submit", event.value, event.touched);
              this.props.signUp(event.value);
            }}
          >
            <FormField label="username" name="username">
              <TextInput name="username" placeholder="Username" />
            </FormField>

            <FormField label="Full Name" name="fullName">
              <TextInput name="fullName" placeholder="Full Name" />
            </FormField>

            <FormField label="Email" name="email">
              <TextInput name="email" placeholder="Email" type="email"/>
            </FormField>

            <FormField label="Password" name="password">
              <TextInput
                placeholder="Password"
                name="password"
                htmlFor="password"
                type="password"
              />
            </FormField>
            <FormField>
              <TextInput
                placeholder="Confirm Password"
                name="password"
                htmlFor="password"
                type="password"
              />
            </FormField>
            <Box gap="small" direction="row">
              <Button color="#D81159" label="Cancel" />
              <Button type="submit" color="#D81159" primary label="Continue" />
            </Box>
          </Form>
        </Box>
      </Box>
    </>
  );
};

export default connect(
    null,
    { signUp }
)(Signup);
