import React from 'react';
import PropTypes from 'prop-types';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import {
  Anchor,
  AnnounceContext,
  Box,
  Card, 
  CardBody, 
  CardFooter,
  Heading,
  Header,
  Grommet,
  Select,
  Text,
  Nav} from 'grommet';
  import {
    Announce,
    Gremlin
  } from 'grommet-icons';
  import Example from './PetProfile';
  import FluffForm from './FluffForm';
  import './index.css';

const Identifier = ({ children, title, subTitle, size, ...rest }) => (
  <Box gap="small" align="center" direction="row" pad="small" {...rest}>
    {children}
    <Box>
      <Text size={size} weight="bold">
        {title}
      </Text>
      <Text size={size}>{subTitle}</Text>
    </Box>
  </Box>
);

const Announcer = ({ announce, message, mode, role }) => {
  React.useEffect(() => {
    const timeout = 3000;
    announce(message, mode, timeout);
  }, [announce, message, mode]);

  return (
    <Text align="center" role={role} aria-live={mode}>
      {message}
    </Text>
  );
};

Announcer.propTypes = {
  announce: PropTypes.func.isRequired,
  message: PropTypes.string,
  mode: PropTypes.string,
  role: PropTypes.string,
};

Announcer.defaultProps = {
  message: 'Here is a simple announcement. This will soon disappear',
  mode: 'polite',
  role: 'log',
};


function App({...props}) {
  const options = ['location', 'size']
  return (
    <Router>
    <Grommet full>
      <Header background="light-4" pad="small">
        <Text weight="bold">Fluff Finder</Text>
      <Nav direction="row">
        <Anchor label="Log in" href="#" color="#5c8001" />
        <Anchor label="Sign up" href="#" color="#5c8001"/>
      </Nav>
    </Header>    
    <Box>

</Box>
<Box justify="center" align="center" background="#fbb02d">
  <Heading color="white">You aint :-o Nicki you :-o Nicole body</Heading>
<AnnounceContext.Consumer>
{announce => <Announcer announce={announce}{...props}  />}
</AnnounceContext.Consumer>
</Box>
<Box direction="row" align="center" justify="center" pad="large">
<Card
pad="large"
            onClick={() => {

              alert('Card was Clicked!');
            }}
          >
            <CardBody pad="small">
              <Identifier
                title={"Find a Fluff"}
                subTitle={"Search for a fluffy friend"}
                size="xlarge"
              >
                {<Gremlin/>}
              </Identifier>
            </CardBody>
            <CardFooter pad={{ horizontal: 'medium', vertical: 'small' }}>
              <Text size="xsmall">{"Have you lost your pet?"}</Text>
            </CardFooter>
          </Card>
          <Link to="/findafluff">
          <Card
          pad="large"
          >
            <CardBody pad="small">
              <Identifier
                title={"Share a Fluff"}
                subTitle={"Tell us if you have spotted a lost pet"}
                size="xlarge"
              >
                {<Gremlin/>}
              </Identifier>
            </CardBody>
            <CardFooter pad={{ horizontal: 'medium', vertical: 'small' }}>
              <Text size="xsmall">{"Have you found someones fluff?"}</Text>
            </CardFooter> 
          </Card>
          </Link>
          </Box>
          <Example/>
    </Grommet>

    <Switch>
      <Route exact path="/findafluff">
        <FluffForm />
      </Route>
    </Switch>
    </Router>
  );
}

export default App;
