import React from 'react';
import {
    Box,
    Grommet,
    Header,
Form,
FormField,
RangeInput,
RadioButtonGroup,
Select,
TextInput,
} from 'grommet';

const FluffForm = () => {
    const [value, setValue] = React.useState({ name: 'a', email: 'b' });
    const options = ["Brawley, CA", "Chico, CA", "San Fransico, CA"]
    return(
        <Grommet>
            <Box justify="center" align="center">
            <Header level={1}> Submit a Fluff </Header>
            </Box>
            <Box justify="center" align="center">
            <Form
                value = {value}
                onChange={nextValue => setValue(nextValue)}
            onSubmit={({ value: nextValue }) => console.log(nextValue)}
                >
            <FormField label="Name" name="name" required>
                <TextInput name="name"/>
            </FormField>
            <FormField label="Size" name="size" pad>
              <RangeInput name="size" min={15} max={75} value={value} step={1} />
            </FormField>
            <FormField name="species">
              <RadioButtonGroup name="species" options={['dog', 'cat']} />
            </FormField>
            <Select
          id="select"
          name="select"
          placeholder="Select"
          value={value}
          options={options}
          onChange={({ option }) => setValue(option)}
          {...rest}
        />
            </Form>
            </Box>
        </Grommet>
    );
};

export default FluffForm;
