import React from 'react';
import {
  Anchor,
  AnnounceContext,
  Box,
  Card, 
  CardBody, 
  CardFooter,
  Header,
  Grommet,
  Select,
  Text,
  Nav} from 'grommet';
  import {
    Announce,
    Gremlin
  } from 'grommet-icons';
import logo from './logo.svg';
import './App.css';
const Identifier = ({ children, title, subTitle, size, ...rest }) => (
  <Box gap="small" align="center" direction="row" pad="small" {...rest}>
    {children}
    <Box>
      <Text size={size} weight="bold">
        {title}
      </Text>
      <Text size={size}>{subTitle}</Text>
    </Box>
  </Box>
);

const Announcer = ({ announce, message, mode, role }) => {
  React.useEffect(() => {
    const timeout = 3000;
    announce(message, mode, timeout);
  }, [announce, message, mode]);

  return (
    <Text align="center" role={role} aria-live={mode}>
      {message}
    </Text>
  );
};

function App() {
  const options = ['location', 'size']
  return (
    <Grommet full>
      <>
      <Header background="light-4" pad="small">
        <Text weight="bold">Fluff Finder</Text>
      <Nav direction="row">
        <Anchor label="Log in" href="#" />
        <Anchor label="Sign up" href="#" />
      </Nav>
    </Header>    
    <Box>
    <Select
    id="text-input"
    options={options}
    placeholder="Search"
/>
</Box>
<Box color="green">
<AnnounceContext>
{announce => <Announcer announce={announce} {...props} />}
</AnnounceContext>
</Box>
<Box direction="row" align="center">
<Card
            onClick={() => {

              alert('Card was Clicked!');
            }}
          >
            <CardBody pad="small">
              <Identifier
                title={"Find a Fluff"}
                subTitle={"Search for a fluffy friend"}
                size="xlarge"
              >
                {<Gremlin/>}
              </Identifier>
            </CardBody>
            <CardFooter pad={{ horizontal: 'medium', vertical: 'small' }}>
              <Text size="xsmall">{"Have you lost your pet?"}</Text>
            </CardFooter>
          </Card>
          <Card
            onClick={() => {

              alert('Card was Clicked!');
            }}
          >
            <CardBody pad="small">
              <Identifier
                title={"Share a Fluff"}
                subTitle={"Tell us if you have spotted a lost pet"}
                size="xlarge"
              >
                {<Gremlin/>}
              </Identifier>
            </CardBody>
            <CardFooter pad={{ horizontal: 'medium', vertical: 'small' }}>
              <Text size="xsmall">{"Have you found someones fluff?"}</Text>
            </CardFooter>
          </Card>
          </Box>
      </>
    </Grommet>
  );
}

export default App;
