import React from "react";
import { Box, Button, Form, FormField, Header, TextInput } from "grommet";

function signUp(userinfo) {
    const newUser = userinfo;
    console.log('function call: ', userinfo);
  fetch("http://localhost:5000/users/add/", {
    method: "POST",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      username: userinfo.username,
      email: userinfo.email,
      password: userinfo.password,
    }),
  })
    .then((response) => response.json())
    .then((data) => {
      console.log("userinfo: ", userinfo);
      console.log("Success:", data);
    })
    .catch((error) => {
      console.error("Error:", error);
    });
}

const Signup = () => {
  return (
    <>
      <Box justify="center" align="center">
        <Box justify="center" align="center" width="medium">
          <Header> Sign up </Header>
          <Form
            onChange={(value) => console.log("Change", value)}
            onSubmit={(event) => {
              console.log("Submit", event.value, event.touched);
              signUp(event.value);
            }}
          >
            <FormField>
              <TextInput name="username" placeholder="Username" />
            </FormField>
            <FormField>
              <TextInput name="fullName" placeholder="Full Name" />
            </FormField>

            <FormField>
              <TextInput name="email" placeholder="Email" />
            </FormField>
            <FormField>
              <TextInput
                placeholder="Password"
                name="password"
                htmlFor="password"
                type="password"
              />
            </FormField>
            <FormField>
              <TextInput
                placeholder="Confirm Password"
                name="password"
                htmlFor="password"
                type="password"
              />
            </FormField>
            <Box gap="small" direction="row">
              <Button color="#D81159" label="Cancel" />
              <Button type="submit" color="#D81159" primary label="Continue" />
            </Box>
          </Form>
        </Box>
      </Box>
    </>
  );
};

export default Signup;
